import React,{useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import {
	CarouselControl,
	Carousel,
	CarouselItem,
	CarouselIndicators,
    Row,Col
} from 'reactstrap';
import Shoe3 from './images/shoe3.jpeg'
import Shoe4 from './images/shoe4.jpeg'

function Carousel_try() {

	// State for Active index
	const [activeIndex, setActiveIndex] = useState(0);


	// Sample items for Carousel
	const items = [
		{
			caption: 'Sample Caption One',
            src:Shoe4,	
		},
		{
			caption: 'Sample Caption Two',
            src:Shoe3,	
		}
	];

	// Items array length
	const itemLength = items.length - 1

	// Previous button for Carousel
	const previousButton = () => {
		
		const nextIndex = activeIndex === 0 ?
			itemLength : activeIndex - 1;
		setActiveIndex(nextIndex);
	}

	// Next button for Carousel
	const nextButton = () => {
		
		const nextIndex = activeIndex === itemLength ?
			0 : activeIndex + 1;
		setActiveIndex(nextIndex);
	}

	// Carousel Item Data
	const carouselItemData = items.map((item) => {
		return (
			<CarouselItem
				key={item.src}
				
			>
				<img src={item.src}  />
			</CarouselItem>
		);
	});

	return (
		<div >
            <Row>
                <Col md="8">
			<h8>ReactJS Reactstrap Carousel Component</h8>
			<Carousel previous={previousButton} next={nextButton}
				activeIndex={activeIndex}>
				<CarouselIndicators items={items} />
				{carouselItemData}
				
			</Carousel>
            </Col>
            </Row>
		</div >
	);
}

export default Carousel_try;
