import React,{useState,useEffect} from 'react'
import {Row,Col,Card,CardBody,CardImg} from 'reactstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Suits from "./suits.jpeg"
import Pro from "./pro.jpeg"


function Adidas_shoe_component() {
    const [isShown,setIsShown] = useState(false)

    const handleMouseEnter = e => {  
        setIsShown(true)
      }
      const handleMouseLeave = e => {
        setIsShown(false)
      }


  return (
    <div>
         <Row>
            <Col>
            <Card >
                <CardImg variant="top" src={Suits} />
                <CardBody> 
                <button onMouseEnter={handleMouseEnter}><img src={Pro} style={{height:"70px",width:"70px"}}/></button>
                <button onMouseEnter={handleMouseLeave}><img src={Suits} style={{height:"70px",width:"70px"}}/> </button>  
                </CardBody>
                {isShown && (
        <div>
          I'll appear when you hover over the button.
        </div>
      )}
                </Card>
            </Col>
            <Col></Col>
            <Col></Col>
         </Row>

    </div>
  )
}

export default Adidas_shoe_component
