import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import {Row,Col,Carousel,CarouselItem,CarouselCaption} from 'reactstrap'
import Shoe1 from './images/shoe1.jpg'
import Shoe2 from './images/shoe2.jpeg'
import Shoe3 from './images/shoe3.jpeg'
import Shoe4 from './images/shoe4.jpeg'

function Component_shoe_1() {
  return (
    <div>
        <Row>
            <Col md="8">
            <Carousel>
  <CarouselItem>
    <img
      
      src={Shoe1}
      alt="First slide"
    />
    <CarouselCaption>
      <h3>First slide label</h3>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </CarouselCaption>
  </CarouselItem>
  <CarouselItem>
    <img
      
      src={Shoe2}
      alt="Second slide"
    />

    <CarouselCaption>
      <h3>Second slide label</h3>
    </CarouselCaption>
  </CarouselItem>
  <CarouselItem>
    <img
     
      src={Shoe4}
      alt="Third slide"
    />

    <CarouselCaption>
      <h3>Third slide label</h3>
      
    </CarouselCaption>
  </CarouselItem>
</Carousel>
            <img src={Shoe3} />
            </Col>
        
        </Row>
    </div>
  )
}

export default Component_shoe_1

