import React,{useState,useEffect} from 'react'
import {Row,Col,Card,CardBody,CardImg,Tooltip} from 'reactstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Suits from "./images/suits.jpeg"
import Pro from "./images/pro.jpeg"


function Hover_div_hide() {
    const [isShown,setIsShown] = useState(false)

    const [initial,finalimage] = useState(Suits)
    const [initialvalue,finalvalue] = useState('--')
    const [initialvalue1,finalvalue1] = useState(200)

    const [tooltipOpen, setTooltipOpen] = React.useState(false);

    const handleMouseEnter = e => {  
        finalimage(Pro);
        
      }
      const handleMouseLeave = e => {
        finalimage(Suits);
       
        // console.log('abc')
      }

     

    // const handleMouseEnter = e => {  
    //     setIsShown(true)
    //   }
    //   const handleMouseLeave = e => {
    //     setIsShown(false)
    //   }


  return (
    <div>
         <Row>
            <Col>
            <Card onMouseEnter={() => setIsShown(true)}
                  onMouseLeave={() => setIsShown(false)} >

<Tooltip isOpen={tooltipOpen}
                delay={{ show:1000, hide: 2000 }}
                target="testID"
                placement="bottom"
                toggle={() => setTooltipOpen(!tooltipOpen)}>
                Sample ToolTip Information...
            </Tooltip>
                <CardImg variant="top" src={initial} />
                <CardBody> 
                  
                </CardBody>
                
                {isShown && (
                 <div>
                <a onMouseEnter={() => finalvalue(true)}
                  onMouseLeave={() => finalvalue(false)}> <img src={Pro}  onMouseEnter={handleMouseEnter} style={{height:"70px",width:"70px"}} /> </a>
               <a onMouseEnter={() => finalvalue1(true)}
                  onMouseLeave={() => finalvalue1(false)}> 
                   <img src={Suits} onMouseEnter={handleMouseLeave} style={{height:"70px",width:"70px"}}/>  </a>
                 
                 </div>
                 )}

                 {initialvalue && (
                 <div>
                price  55$
                 </div>
                 )}

                 {initialvalue1 && (
                 <div>
                 price 50$
                 </div>
                 )}


                 <h6>jackets and coats</h6>
                 <a>fashonable and luxurious</a>
            </Card>
            </Col>
            <Col></Col>
            <Col></Col>
         </Row>

    </div>
  )
}

export default Hover_div_hide
