import React,{useState,useEffect} from 'react'
import {Row,Col,Card,CardBody,CardImg} from 'reactstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Suits from "./images/suits.jpeg"
import Pro from "./images/pro.jpeg"


function Hover_div_hide() {
    const [isShown,setIsShown] = useState(false)

    const [initial,finalimage] = useState(Suits)
    const [initialvalue,finalvalue] = useState(200)

    const handleMouseEnter = e => {  
        finalimage(Pro)
      }
      const handleMouseLeave = e => {
        finalimage(Suits)
      }

    // const handleMouseEnter = e => {  
    //     setIsShown(true)
    //   }
    //   const handleMouseLeave = e => {
    //     setIsShown(false)
    //   }


  return (
    <div>
         <Row>
            <Col>
            <Card onMouseEnter={() => setIsShown(true)}
                  onMouseLeave={() => setIsShown(false)} >
                <CardImg variant="top" src={initial} />
                <CardBody> 
                  
                </CardBody>
                {initialvalue}
                {isShown && (
                 <div>
               <img src={Pro} onMouseEnter={handleMouseEnter} style={{height:"70px",width:"70px"}}/>
               <img src={Suits} onMouseEnter={handleMouseLeave} style={{height:"70px",width:"70px"}}/>  
                 
                 </div>
                 )}
                 <h6>jackets and coats</h6>
                 <a>fashonable and luxurious</a>
            </Card>
            </Col>
            <Col></Col>
            <Col></Col>
         </Row>

    </div>
  )
}

export default Hover_div_hide
