import React,{useState,useEffect} from 'react'
import {Row,Col,Card,CardBody,CardImg} from 'reactstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Suits from "./suits.jpeg"
import Pro from "./pro.jpeg"


function Adidas_shoe_component() {
    const [initial,finalimage] = useState(Suits)

    const handleMouseEnter = e => {  
        finalimage(Pro)
      }
      const handleMouseLeave = e => {
        finalimage(Suits)
      }


  return (
    <div>
         <Row>
            <Col>
            <Card  
         >
                <CardImg variant="top" src={initial} />
                <CardBody>
                <a>Shoe</a>
                <button onMouseEnter={handleMouseEnter}>image 1</button>
                <button onMouseEnter={handleMouseLeave}> image 2 </button>  
                </CardBody>
                </Card>
            </Col>
            <Col></Col>
            <Col></Col>
         </Row>

    </div>
  )
}

export default Adidas_shoe_component
